let shape; // Global variable for the shape

function setup() {
    createCanvas(500, 400); // Create a 500x400 canvas
    shape = new MovingShape(width / 2, height / 2, 50, 3, 2, 'blue'); // Create shape
}

function draw() {
    background(240); // Clear screen
    shape.update(); // Update shape position
    shape.show(); // Draw shape
}

function mousePressed() {
    shape.changeSpeed(); // Change shape speed when clicked
}
